import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CartService } from '../service/cart.service';
import { OrderService } from '../service/order.service';
import { Customer } from '../model/customer';
import { Order } from '../model/order';
import { OrderItem } from '../model/order-item';
import { CartItem } from '../model/cart-item';

@Component({
  selector: 'app-product-order',
  templateUrl: './product-order.component.html',
  styleUrls: ['./product-order.component.css']
})
export class ProductOrderComponent implements OnInit {

  checkoutForm!: FormGroup; // The '!' means it will be initialized in ngOnInit
  totalPrice: number = 0;
  totalQuantity: number = 0;

  isOrderPlaced: boolean = false; // To show a "thank you" message

  constructor(
    private formBuilder: FormBuilder,
    private cartService: CartService,
    private orderService: OrderService,
    private router: Router
  ) { }

  ngOnInit(): void {
    // Build the checkout form
    this.checkoutForm = this.formBuilder.group({
      customer: this.formBuilder.group({
        firstName: ['', [Validators.required, Validators.minLength(2)]],
        lastName: ['', [Validators.required, Validators.minLength(2)]],
        email: ['', [Validators.required, Validators.pattern('^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$')]]
      })
      // We would add shipping/billing address groups here too
    });

    // Get the totals from the cart service
    this.reviewCartDetails();
  }

  reviewCartDetails() {
    this.cartService.totalPrice.subscribe(data => this.totalPrice = data);
    this.cartService.totalQuantity.subscribe(data => this.totalQuantity = data);
    this.cartService.computeCartTotals(); // Ensure totals are up-to-date
  }

  // --- Form Getters ---
  // These make it easier to access form controls in the HTML
  get firstName() { return this.checkoutForm.get('customer.firstName'); }
  get lastName() { return this.checkoutForm.get('customer.lastName'); }
  get email() { return this.checkoutForm.get('customer.email'); }
  // ---

  onSubmit() {
    console.log("Submitting order...");

    // Stop if the form is invalid
    if (this.checkoutForm.invalid) {
      this.checkoutForm.markAllAsTouched(); // Show validation errors
      console.warn("Form is invalid!");
      return;
    }

    // 1. Set up the Order
    let order = new Order();

    // 2. Get Customer from form
    order.customer = this.checkoutForm.get('customer')?.value;

    // 3. Get Cart Items from service
    const cartItems: CartItem[] = this.cartService.getItems();

    // 4. Map CartItems to OrderItems (the DTO for the backend)
    order.orderItems = cartItems.map(item => new OrderItem(item));

    // 5. Set totals on the order
    order.totalPrice = this.totalPrice;
    order.totalQuantity = this.totalQuantity;

    // 6. Call the OrderService to send the data to the backend
    this.orderService.placeOrder(order).subscribe({
      //
      // --- FIX 1: Added ': any' to response ---
      //
      next: (response: any) => {
        console.log("Order placed successfully!", response);

        // Show success message
        this.isOrderPlaced = true; 

        // Clear the cart
        this.cartService.clearCart();

        // Redirect to home page after a few seconds
        setTimeout(() => {
          this.router.navigate(['/']);
        }, 3000); // 3 seconds
      },
      //
      // --- FIX 2: Added ': any' to err ---
      //
      error: (err: any) => {
        console.error("Error placing order:", err);
        alert("There was an error placing your order. Please try again.");
      }
    });
  }
}

<div class="checkout-container">
  <h1>Checkout</h1>

  <!-- 
    Show this "Thank You" message AFTER the order is placed.
    The 'isOrderPlaced' flag controls this.
  -->
  <div *ngIf="isOrderPlaced" class="success-message">
    <h2>Thank you for your order!</h2>
    <p>Your order has been received and is being processed.</p>
    <p>Redirecting you to the home page...</p>
  </div>

  <!-- 
    Show the form BEFORE the order is placed.
  -->
  <div *ngIf="!isOrderPlaced" class="checkout-layout">

    <!-- Left Column: Checkout Form -->
    <div class="form-column">
      <form [formGroup]="checkoutForm" (ngSubmit)="onSubmit()">

        <!-- Customer Info Section -->
        <div formGroupName="customer" class="form-section">
          <h2>Customer Information</h2>

          <!-- First Name -->
          <div class="form-field">
            <label for="firstName">First Name</label>
            <input id="firstName" type="text" formControlName="firstName">
            <div *ngIf="firstName?.invalid && (firstName?.dirty || firstName?.touched)" class="validation-error">
              <div *ngIf="firstName?.errors?.['required']">First name is required.</div>
              <div *ngIf="firstName?.errors?.['minlength']">First name must be at least 2 characters.</div>
            </div>
          </div>

          <!-- Last Name -->
          <div class="form-field">
            <label for="lastName">Last Name</label>
            <input id="lastName" type="text" formControlName="lastName">
            <div *ngIf="lastName?.invalid && (lastName?.dirty || lastName?.touched)" class="validation-error">
              <div *ngIf="lastName?.errors?.['required']">Last name is required.</div>
              <div *ngIf="lastName?.errors?.['minlength']">Last name must be at least 2 characters.</div>
            </div>
          </div>

          <!-- Email -->
          <div class="form-field">
            <label for="email">Email</label>
            <input id="email" type="email" formControlName="email">
            <div *ngIf="email?.invalid && (email?.dirty || email?.touched)" class="validation-error">
              <div *ngIf="email?.errors?.['required']">Email is required.</div>
              <div *ngIf="email?.errors?.['pattern']">Please enter a valid email address.</div>
            </div>
          </div>
        </div>

        <!-- 
          You would add more form sections here for:
          - Shipping Address
          - Billing Address
          - Payment Info
        -->

        <!-- Submit Button -->
        <div class="form-actions">
          <button type="submit" class="btn btn-primary" [disabled]="checkoutForm.invalid">Place Order</button>
        </div>
      </form>
    </div>

    <!-- Right Column: Order Summary -->
    <div class="summary-column">
      <div class="summary-box">
        <h2>Order Summary</h2>
        <div class="summary-item">
          <span>Total Quantity:</span>
          <strong>{{ totalQuantity }}</strong>
        </div>
        <div class="summary-item total">
          <span>Total Price:</span>
          <strong>{{ totalPrice | currency:'₱' }}</strong>
        </div>
        <hr>
        <p class="summary-note">
          Shipping and taxes will be calculated at the next step (or are included, etc.)
        </p>
      </div>
      <a routerLink="/cart" class="back-link">&laquo; Back to Cart</a>
    </div>

  </div>
</div>
