import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Order } from '../model/order';

@Injectable({
  providedIn: 'root'
})
export class OrderService {

  // The URL to your backend's "create order" endpoint
  private orderUrl = 'http://localhost:8080/api/order';

  constructor(private http: HttpClient) { }

  /**
   * Sends the completed Order object to the backend.
   * @param order The Order DTO, containing the Customer and OrderItem list.
   * @returns An Observable of the saved Order (as confirmation).
   */
  placeOrder(order: Order): Observable<Order> {
    // We send the whole 'order' object, which matches our backend
    return this.http.post<Order>(this.orderUrl, order);
  }
}
