import { Product } from "./product";

export class CartItem {
  id: number;
  name: string;
  imageFile: string;
  unitPrice: number; // <-- unitPrice is a number
  quantity: number;

  constructor(product: Product) {
    this.id = product.id; 
    this.name = product.name;
    this.imageFile = product.imageFile;
    
    // *** THIS IS THE FIX ***
    // Convert the 'price' string from the Product model to a number
    this.unitPrice = parseFloat(product.price); 
    
    this.quantity = 1;
  }
}
