import { Component, OnInit, OnDestroy } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CartService } from '../service/cart.service';
import { OrderService } from '../service/order.service';
import { Order } from '../model/order';
import { OrderItem } from '../model/order-item';
import { Customer } from '../model/customer';
import { Subscription } from 'rxjs';
import { CartItem } from '../model/cart-item';

@Component({
  selector: 'app-product-order',
  templateUrl: './product-order.component.html',
  styleUrls: ['./product-order.component.css']
})
export class ProductOrderComponent implements OnInit, OnDestroy {

  checkoutForm!: FormGroup;
  totalPrice: number = 0;
  totalQuantity: number = 0;
  isOrderPlaced: boolean = false;

  private cartSub: Subscription = new Subscription();

  constructor(
    private formBuilder: FormBuilder,
    private cartService: CartService,
    private orderService: OrderService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.checkoutForm = this.formBuilder.group({
      firstname: ['', [Validators.required, Validators.minLength(2)]],
      middlename: [''],
      lastname: ['', [Validators.required, Validators.minLength(2)]],
      dateOfBirth: ['', Validators.required],
      gender: ['', Validators.required]
    });

    this.cartSub.add(
      this.cartService.totalPrice$.subscribe((data: number) => this.totalPrice = data)
    );
    this.cartSub.add(
      this.cartService.totalQuantity$.subscribe((data: number) => this.totalQuantity = data)
    );

    if (this.cartService.getCartItems().length === 0) {
      this.router.navigate(['/product']);
    }
  }

  ngOnDestroy(): void {
    this.cartSub.unsubscribe();
  }

  get firstname() { return this.checkoutForm.get('firstname'); }
  get lastname() { return this.checkoutForm.get('lastname'); }
  get dateOfBirth() { return this.checkoutForm.get('dateOfBirth'); }
  get gender() { return this.checkoutForm.get('gender'); }

  onSubmit() {
    console.log("Submitting order...");

    if (this.checkoutForm.invalid) {
      this.checkoutForm.markAllAsTouched();
      console.warn("Form is invalid!");
      return;
    }

    const cartItems = this.cartService.getCartItems();

    let order = new Order();
    order.totalPrice = this.totalPrice;
    order.totalQuantity = this.totalQuantity;

    let customer = new Customer();
    customer.firstname = this.firstname?.value;
    customer.middlename = this.checkoutForm.get('middlename')?.value;
    customer.lastname = this.lastname?.value;
    customer.dateOfBirth = this.dateOfBirth?.value;
    customer.gender = this.gender?.value;

    order.customer = customer;

    order.items = cartItems.map((cartItem: CartItem) => {
      let item = new OrderItem();
      item.productId = cartItem.id;
      item.productName = cartItem.name;
      item.productDescription = cartItem.description;
      item.productCategoryName = cartItem.categoryName;
      item.productImageFile = cartItem.imageFile;
      item.productUnitOfMeasure = cartItem.unitOfMeasure;
      item.quantity = cartItem.quantity;
      item.price = cartItem.unitPrice;
      return item;
    });

    this.orderService.placeOrder(order).subscribe({
      next: (response: any) => {
        console.log("Order placed successfully!", response);
        this.isOrderPlaced = true;
        this.cartService.clearCart(); 

        setTimeout(() => {
          this.router.navigate(['/']);
        }, 3000);
      },
      error: (err: any) => {
        console.error("Error placing order:", err);
        alert("There was an error placing your order. Please try again.");
      }
    });
  }
}

<div class="checkout-container" *ngIf="!isOrderPlaced">
  <h1>Checkout</h1>

  <form [formGroup]="checkoutForm" (ngSubmit)="onSubmit()">
    <div class="checkout-layout">

      <div class="form-column">
        <div class="form-section">
          <h2>Customer Information</h2>

          <div class="form-field">
            <label for="firstname">First Name</label>
            <input id="firstname" type="text" formControlName="firstname"
                   [ngClass]="{ 'is-invalid': firstname?.invalid && (firstname?.dirty || firstname?.touched) }">

            <div *ngIf="firstname?.invalid && (firstname?.dirty || firstname?.touched)" class="validation-error">
              <div *ngIf="firstname?.errors?.['required']">
                First Name is required.
              </div>
              <div *ngIf="firstname?.errors?.['minlength']">
                First Name must be at least 2 characters long.
              </div>
            </div>
          </div>

          <div class="form-field">
            <label for="middlename">Middle Name (Optional)</label>
            <input id="middlename" type="text" formControlName="middlename">
          </div>

          <div class="form-field">
            <label for="lastname">Last Name</label>
            <input id="lastname" type="text" formControlName="lastname"
                   [ngClass]="{ 'is-invalid': lastname?.invalid && (lastname?.dirty || lastname?.touched) }">

            <div *ngIf="lastname?.invalid && (lastname?.dirty || lastname?.touched)" class="validation-error">
              <div *ngIf="lastname?.errors?.['required']">
                Last Name is required.
              </div>
              <div *ngIf="lastname?.errors?.['minlength']">
                Last Name must be at least 2 characters long.
              </div>
            </div>
          </div>

          <div class="form-field">
            <label for="dateOfBirth">Date of Birth</label>
            <input id="dateOfBirth" type="date" formControlName="dateOfBirth"
                   [ngClass]="{ 'is-invalid': dateOfBirth?.invalid && (dateOfBirth?.dirty || dateOfBirth?.touched) }">

            <div *ngIf="dateOfBirth?.invalid && (dateOfBirth?.dirty || dateOfBirth?.touched)" class="validation-error">
              <div *ngIf="dateOfBirth?.errors?.['required']">
                Date of Birth is required.
              </div>
            </div>
          </div>

          <div class="form-field">
            <label for="gender">Gender</label>
            <select id="gender" formControlName="gender"
                    [ngClass]="{ 'is-invalid': gender?.invalid && (gender?.dirty || gender?.touched) }">
              <option value="" disabled>Select a gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
              <option value="PreferNotToSay">Prefer not to say</option>
            </select>

            <div *ngIf="gender?.invalid && (gender?.dirty || gender?.touched)" class="validation-error">
              <div *ngIf="gender?.errors?.['required']">
                Please select a gender.
              </div>
            </div>
          </div>

        </div>
      </div>

      <div class="summary-column">
        <div class="summary-box">
          <h2>Order Summary</h2>

          <div class="summary-item">
            <span>Total Quantity:</span>
            <span>{{ totalQuantity }}</span>
          </div>

          <div class="summary-item total">
            <span>Total Price:</span>
            <span>{{ totalPrice | currency:'₱' }}</span>
          </div>

          <p class="summary-note">
            Shipping and taxes are included.
          </p>

          <a routerLink="/cart" class="back-link">&laquo; Back to Cart</a>

          <div class="form-actions">
            <button type="submit" class="btn-primary" [disabled]="checkoutForm.invalid">
              Place Order
            </button>
          </div>
        </div>
      </div>

    </div>
  </form>
</div>

<div *ngIf="isOrderPlaced" class="success-message">
  <h2>Thank you for your order!</h2>
  <p>Your order has been received and is being processed.</p>
  <p>You will be redirected to the home page shortly.</p>
</div>
