import { Customer } from "./customer";
import { OrderItem } from "./order-item";

/**
 * This is the main "wrapper" DTO sent to the backend during checkout.
 * This MUST match the Order.java DTO in Spring.
 */
export class Order {
  id: number = 0;
  customer: Customer = new Customer();
  items: OrderItem[] = []; // NOT orderItems
  totalPrice: number = 0;
  totalQuantity: number = 0;
}
