import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CartService } from '../service/cart.service';
import { OrderService } from '../service/order.service';
import { Order } from '../model/order';
import { OrderItem } from '../model/order-item';

@Component({
  selector: 'app-product-order',
  templateUrl: './product-order.component.html',
  styleUrls: ['./product-order.component.css']
})
export class ProductOrderComponent implements OnInit {

  checkoutForm!: FormGroup;
  totalPrice: number = 0;
  totalQuantity: number = 0;
  isOrderPlaced: boolean = false;

  constructor(
    private formBuilder: FormBuilder,
    private cartService: CartService,
    private orderService: OrderService,
    private router: Router
  ) { }

  ngOnInit(): void {
    // Build the checkout form with a single "fullName" field
    this.checkoutForm = this.formBuilder.group({
      fullName: ['', [Validators.required, Validators.minLength(2)]]
    });

    this.reviewCartDetails();
  }

  reviewCartDetails() {
    this.cartService.totalPrice.subscribe(data => this.totalPrice = data);
    this.cartService.totalQuantity.subscribe(data => this.totalQuantity = data);
    this.cartService.computeCartTotals();
  }

  // --- Form Getter ---
  get fullName() { return this.checkoutForm.get('fullName'); }
  // ---

  //
  // --- THIS IS OUR NEW HARDCODED TEST FUNCTION ---
  //
  runHardcodedTest() {
    console.log("--- RUNNING HARDCODED TEST ---");

    // 1. Create a single hardcoded OrderItem
    let testItem = new OrderItem();
    testItem.customerId = 99; // Test ID
    testItem.customerName = "HARDCODED TESTER";
    testItem.productId = 1; // Assuming a product with ID 1 exists
    testItem.productName = "Test Product";
    testItem.productDescription = "Test Description";
    testItem.productCategoryName = "Test Category";
    testItem.productImageFile = "test_image";
    testItem.productUnitOfMeasure = "pcs";
    testItem.quantity = 1; // 1 item
    testItem.price = 12.34; // Test price

    // 2. Create the main Order object
    let testOrder = new Order();
    testOrder.totalPrice = 12.34;
    testOrder.totalQuantity = 1;

    // 3. Add the test item to the order's item list
    // The backend expects a list, even if it's just one item
    testOrder.orderItems = [testItem]; 

    // 4. Call the OrderService
    this.orderService.placeOrder(testOrder).subscribe({
      next: (response: any) => {
        console.log("--- TEST SUCCESS ---", response);
        alert("Hardcoded test was SUCCESSFUL! Check your database.");
      },
      error: (err: any) => {
        console.error("--- TEST FAILED ---", err);
        alert("Hardcoded test FAILED. Check console (F12) for errors.");
      }
    });
  }

  //
  // --- THIS IS THE REAL SUBMIT FUNCTION ---
  //
  onSubmit() {
    console.log("Submitting order...");

    if (this.checkoutForm.invalid) {
      this.checkoutForm.markAllAsTouched();
      console.warn("Form is invalid!");
      return;
    }

    // 1. Get Customer Name from form
    const customerNameFromForm = this.checkoutForm.get('fullName')?.value;

    // 2. Get Cart Items from service
    const cartItems = this.cartService.getItems();

    // 3. Create the Order
    let order = new Order();
    order.totalPrice = this.totalPrice;
    order.totalQuantity = this.totalQuantity;

    // 4. Map CartItems to OrderItems
    // This is where we add the customer name to *each* item
    order.orderItems = cartItems.map(cartItem => {
      let item = new OrderItem();

      // --- Map Product data ---
      item.productId = cartItem.id;
      item.productName = cartItem.name;
      item.productDescription = "N/A"; // Backend might not need this
      item.productCategoryName = "N/A"; // Backend might not need this
      item.productImageFile = cartItem.imageFile;
      item.productUnitOfMeasure = "N/A"; // Or get from cartItem if it exists
      item.quantity = cartItem.quantity;
      item.price = cartItem.unitPrice;

      // --- Map Customer data (from form) ---
      item.customerName = customerNameFromForm;
      item.customerId = 0; // Backend should probably generate/assign this

      return item;
    });

    // 5. Call the OrderService
    this.orderService.placeOrder(order).subscribe({
      next: (response: any) => {
        console.log("Order placed successfully!", response);
        this.isOrderPlaced = true;
        this.cartService.clearCart(); // Clear the temp cart

        setTimeout(() => {
          this.router.navigate(['/']);
        }, 3000);
      },
      error: (err: any) => {
        console.error("Error placing order:", err);
        alert("There was an error placing your order. Please try again.");
      }
    });
  }
}

