import { CartItem } from "./cart-item";

/**
 * Represents a single item in an order.
 * This structure is designed to be sent to your backend 
 * and match your `order_item_data` table.
 */
export class OrderItem {
    // These properties come from the cart item
    productId: number;
    productName: string;
    productImageFile: string;
    price: number;
    quantity: number;
    
    // These properties might be on your product/cart item
    // Note: Your DB screenshot showed these, so we'll map them.
    productCategoryName: string = ''; 
    productDescription: string = '';
    productUnitOfMeasure: string = '';

    constructor(cartItem: CartItem) {
        this.productId = cartItem.id;
        this.productName = cartItem.name;
        this.productImageFile = cartItem.imageFile;
        this.price = cartItem.unitPrice;
        this.quantity = cartItem.quantity;
        
        // You would need to get these values from your Product/CartItem
        // if they exist there. For now, I'll stub them.
        // If your CartItem had the full Product, you'd map them here.
        // this.productCategoryName = cartItem.categoryName; 
        // this.productDescription = cartItem.description;
    }
}
