import { Injectable } from '@angular/core';
import { BaseHttpService } from './base-http.service';
import { HttpClient } from '@angular/common/http';
import { Order } from '../model/order';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class OrderService extends BaseHttpService {

  // We still need the constructor to set the path
  constructor(protected override http: HttpClient) { 
    // This sets this.apiServerUrl and this.path
    super(http, '/api/order');
  }

  /**
   * Sends the completed order to the backend to be saved.
   * @param order The final order object
   * @returns An observable of the saved order
   */
  placeOrder(order: Order): Observable<any> {
    //
    // --- THIS IS THE NEW FIX ---
    // Instead of calling the inherited `this.update()`, we are
    // calling `this.http.post()` directly. This MUST resolve
    // any inheritance or caching issues.
    //
    // It uses `this.apiServerUrl` and `this.path` from the BaseHttpService.
    //
    return this.http.post<any>(this.apiServerUrl + this.path, order);
  }
}
