import { Injectable } from '@angular/core';
import { BaseHttpService } from './base-http.service';
import { HttpClient } from '@angular/common/http';
import { Order } from '../model/order';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class OrderService extends BaseHttpService {

  //
  // --- FIX 1 ---
  // The constructor path must match your new controller's endpoint.
  //
  constructor(protected override http: HttpClient) { 
    // This now points to your @PutMapping("/api/orderitems")
    super(http, '/api/orderitems'); 
  }

  /**
   * Places an order.
   * This method acts as a "translator". The component gives it a full
   * 'Order' object, but this method extracts *only* the 'orderItems' list
   * and sends that to the backend, which is what your controller expects.
   * * @param order The final order object from the component
   * @returns An observable from the HTTP request
   */
  public placeOrder(order: Order): Observable<any> {

    //
    // --- FIX 2 ---
    // We call `this.add()` (which uses PUT) and pass *only* the
    // list of items, which is what your @PutMapping expects.
    //
    return this.add(order.orderItems);
  }
}
