import { Component, OnInit } from '@angular/core';
import { ProductCategory } from '../model/product-category';
import { ProductService } from '../service/product.service';
import { Product } from '../model/product';
import { CartService } from '../service/cart.service';

@Component({
  selector: 'app-product-category',
  templateUrl: './product-category.component.html',
  styleUrls: ['./product-category.component.css']
})
export class ProductCategoryComponent implements OnInit {

  public allProductsCategory: ProductCategory[] = []; 
  public searchTerm: string = '';                   
  public selectedCategory: string = '';             

  public categoriesForFilter: ProductCategory[] = [];

  constructor(
    private productService: ProductService,
    private cartService: CartService 
  ) {}

  ngOnInit(): void {
    console.log("ngOnInit called");
    this.productService.getData().subscribe(data => {
      this.allProductsCategory = data;
      this.categoriesForFilter = [...data];
      console.log("Data Received:", this.allProductsCategory);
    });
  }

  addToCart(product: Product) {
    console.log(`Adding to cart: ${product.name}, ${product.price}`);
    this.cartService.addToCart(product);
  }

  get filteredProductsCategory(): ProductCategory[] {
    if (!this.allProductsCategory) {
      return []; 
    }

    let filteredList = this.allProductsCategory;

    if (this.selectedCategory) {
      filteredList = filteredList.filter(category =>
        category.categoryName === this.selectedCategory
      );
    }

    if (this.searchTerm) {
      const lowerSearchTerm = this.searchTerm.toLowerCase().trim();
      if (lowerSearchTerm === '') {
         return filteredList;
      }

      filteredList = filteredList.map(category => {
        const filteredProducts = category.products.filter(product =>
          product.name.toLowerCase().includes(lowerSearchTerm) ||
          product.description.toLowerCase().includes(lowerSearchTerm)
        );

        if (filteredProducts.length > 0) {
           return { categoryName: category.categoryName, products: filteredProducts };
        }
        return null; 

      }).filter(category => category !== null) as ProductCategory[]; 
    }

    return filteredList;
  }
}

<div class="product-page-container">

  <div class="page-header">
    <h1>The Gear We Trust</h1>
    <p>Browse our meticulously curated selection of essential tools for construction.</p>
  </div>

  <div class="control-panel">
    <div class="search-bar">
      <input type="text"
             placeholder="Search by name or description..."
             class="search-input"
             [(ngModel)]="searchTerm">
    </div>

    <div class="filter-controls">
      <label for="category-filter">Filter by Category:</label>
      <select id="category-filter" class="filter-select" [(ngModel)]="selectedCategory">
        <option value="">All Categories</option>
        <option *ngFor="let category of categoriesForFilter" [value]="category.categoryName">{{ category.categoryName }}</option>
      </select>
    </div>
  </div>

  <div *ngIf="filteredProductsCategory.length === 0 && allProductsCategory.length > 0" class="no-results">
      <p>No products match your current search or filter criteria.</p>
  </div>


  <div *ngFor="let category of filteredProductsCategory" class="category-section">

    <h2 class="category-title">{{ category.categoryName }}</h2>
    <hr>

    <div class="product-grid">
      <div *ngFor="let prod of category.products" class="product-card">

        <img class="product-image"
             [src]="'assets/products/' + prod.imageFile + '.jpg'"
             [alt]="prod.name"
             onerror="this.src='https://placehold.co/200x180/4a3f35/F8F6F4?text=No+Image'">

        <div class="product-info">
          <h3 class="product-name">{{ prod.name | titlecase }}</h3>
          <p class="product-description">{{ prod.description }}</p>
          <p class="product-category">Category: {{ prod.categoryName }}</p>
          <p class="product-unit">Unit: {{ prod.unitOfMeasure }}</p>

          <div class="product-footer">
            <span class="product-price">{{ prod.price | currency:'₱' }}</span>

            <button class="add-to-cart-btn" (click)="addToCart(prod)">
              <img class="cart-icon" src="assets/icons/shoppingcart.svg" alt="Cart">
            </button>
          </div>

        </div>
      </div>

    </div>
  </div>

</div>
