import { Customer } from "./customer";
import { OrderItem } from "./order-item";

/**
 * This is the main "payload" object we send to the backend.
 * It contains the customer and all the items in their order.
 */
export class Order {
    customer: Customer = new Customer();
    orderItems: OrderItem[] = [];
    
    // Your backend might also want a total price/quantity
    totalPrice: number = 0;
    totalQuantity: number = 0;
}
