import { Component, OnInit } from '@angular/core';
import { CartService } from '../service/cart.service';
import { CartItem } from '../model/cart-item';

@Component({
  selector: 'app-shopping-cart',
  templateUrl: './shopping-cart.component.html',
  styleUrls: ['./shopping-cart.component.css']
})
export class ShoppingCartComponent implements OnInit {

  cartItems: CartItem[] = [];
  totalPrice: number = 0;
  totalQuantity: number = 0;

  constructor(private cartService: CartService) { }

  ngOnInit(): void {
    this.listCartDetails();
  }

  /**
   * Gets the cart details from the service and subscribes to future updates.
   */
  listCartDetails() {
    // 1. Get the items from the service's memory
    this.cartItems = this.cartService.getItems();

    // 2. Subscribe to the totals so this page updates automatically
    this.cartService.totalPrice.subscribe(data => this.totalPrice = data);
    this.cartService.totalQuantity.subscribe(data => this.totalQuantity = data);

    // 3. Manually compute totals once on load (in case we landed here directly)
    this.cartService.computeCartTotals();
  }

  /**
   * Calls the service to increment the quantity of an item.
   * @param cartItem The item to increment.
   */
  incrementQuantity(cartItem: CartItem) {
    this.cartService.incrementQuantity(cartItem);
  }

  /**
   * Calls the service to decrement the quantity of an item.
   * @param cartItem The item to decrement.
   */
  decrementQuantity(cartItem: CartItem) {
    this.cartService.decrementQuantity(cartItem);
  }

  /**
   * Calls the service to remove an item from the cart.
   * @param cartItem The item to remove.
   */
  remove(cartItem: CartItem) {
    this.cartService.removeItem(cartItem);
  }
}

<div class="cart-container">
  <h1>Your Shopping Cart</h1>

  <!-- Show this message if the cart is empty -->
  <div *ngIf="cartItems.length === 0" class="empty-cart">
    <p>Your cart is currently empty.</p>
    <a routerLink="/product" class="btn btn-primary">Continue Shopping</a>
  </div>

  <!-- Show this table if the cart has items -->
  <div *ngIf="cartItems.length > 0">
    <table class="cart-table">
      <thead>
        <tr>
          <th>Product</th>
          <th></th>
          <th>Price</th>
          <th>Quantity</th>
          <th>Subtotal</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        <!-- Loop over all items in the cart -->
        <tr *ngFor="let item of cartItems">

          <!-- Image -->
          <td>
            <img [src]="'assets/products/' + item.imageFile + '.jpg'" 
                 class="cart-item-image"
                 onerror="this.src='https://placehold.co/100x100/4a3f35/F8F6F4?text=No+Image'">
          </td>

          <!-- Name -->
          <td class="item-name">{{ item.name | titlecase }}</td>

          <!-- Price -->
          <td>{{ item.unitPrice | currency:'₱' }}</td>

          <!-- Quantity Controls -->
          <td>
            <div class="quantity-controls">
              <button (click)="decrementQuantity(item)" class="quantity-btn" [disabled]="item.quantity === 1">-</button>
              <span class="item-quantity">{{ item.quantity }}</span>
              <button (click)="incrementQuantity(item)" class="quantity-btn">+</button>
            </div>
          </td>

          <!-- Subtotal -->
          <td>{{ (item.unitPrice * item.quantity) | currency:'₱' }}</td>

          <!-- Remove Button -->
          <td>
            <button (click)="remove(item)" class="remove-btn">Remove</button>
          </td>
        </tr>
      </tbody>
    </table>

    <!-- Cart Totals and Checkout -->
    <div class="cart-summary">
      <div class="totals">
        <p><strong>Total Quantity:</strong> {{ totalQuantity }}</p>
        <p><strong>Total Price:</strong> {{ totalPrice | currency:'₱' }}</p>
      </div>
      <div class="checkout-actions">
        <a routerLink="/product" class="btn btn-secondary">Continue Shopping</a>
        <!-- This links to your product-order component -->
        <a routerLink="/order" class="btn btn-primary">Proceed to Checkout</a>
      </div>
    </div>
  </div>
</div>
