import { Product } from "./product";

export class CartItem {
  id: number;
  name: string;
  imageFile: string;
  unitPrice: number;
  quantity: number;

  // --- NEW FIELDS ---
  // We add these so we can pass them to the backend
  description: string;
  categoryName: string;
  unitOfMeasure: string;

  constructor(product: Product) {
    this.id = product.id;
    this.name = product.name;
    this.imageFile = product.imageFile;
    // We must parse the string price to a number
    this.unitPrice = parseFloat(product.price); 
    this.quantity = 1;

    // --- COPY NEW FIELDS ---
    this.description = product.description;
    this.categoryName = product.categoryName;
    this.unitOfMeasure = product.unitOfMeasure;
  }
}
